import { createRouter, createWebHistory } from 'vue-router';
import Home from './components/Home.vue';
import Dashboard from './components/Dashboard.vue';

const routes = [
    { path: '/', component: Home },
    {
        path: '/dashboard',
        component: Dashboard,
        beforeEnter: async (to, from, next) => {
            const token = yourVueInstance.token; // Token'ı doğrudan bileşenden al
            if (token) {
                try {
                    const response = await axios.get('/api/validate-token', {
                        params: { token }
                    });
                    if (response.data.valid) {
                        next(); // Geçerli token varsa devam et
                    } else {
                        next('/'); // Geçersiz token varsa anasayfaya yönlendir
                    }
                } catch (error) {
                    console.error(error);
                    next('/'); // API çağrısında hata varsa anasayfaya yönlendir
                }
            } else {
                next('/'); // Token yoksa anasayfaya yönlendir
            }
        }
    },
];

const router = createRouter({
    history: createWebHistory(),
    routes,
});

export default router;
